parameters.jsCode:
const chunksId = $input.all().map(item => item.json.chunks.map(chunk => chunk.metadata.chunk_id)).flat()

for (const item of $input.all()) {
  item.json.chunks.map(chunk => chunk.metadata.related_chunks = chunksId)
}

return $input.all();

return $input.all().map(item => {
  const metadata = item.json.metadata
  const embed = item.json.embedding

  return {
    id : metadata.chunk_id,
    vector : embed,
    payload : {
    product_id: metadata.product_id,
    product_name: metadata.product_name,
    chunk_type: metadata.chunk_type,
    content: metadata.content,
    source_file: metadata.source_file,
    timestamp: $now.toMillis,
    embedding_status: "done",
    related_chunks: metadata.related_chunks
    }
  }
})
